flow.chain:
//年龄分组
ageGroup = WHEN(babyGroup,childrenGroup,juniorGroup,youthGroup,midLifeGroup,oldAgeGroup);

//主诉
symptomGroup = WHEN(symptomA,symptomB).any(true);

//年龄分组2
ageGroup2 =
SWITCH(ageSwitchGroup).TO(babyGroup,childrenGroup,juniorGroup,youthGroup,midLifeGroup,oldAgeGroup).DEFAULT(defAgeGroup);

//药品
drugGroup = IF(drugIfGroup,drugAGroup,drugBGroup);

//检验
examineGroup = IF(examineIfGroup,examineBGroup);

//主线
THEN(initChain,personGroup,sexGroup,symptomGroup,ageGroup,ageGroup2,drugGroup,examineGroup,ruleGroup);
